import Api from "../utils/Api"
export default function Main (props) {
    const [userName, setUserName] = React.useState('');
    const [userDescription, setUserDescription] = React.useState('');
    const [userAvatar, setUserAvatar] = React.useState('');
    React.useEffect(()=> {
        Api.getInfo()
        .then((data) => {
            const userData = data
            setUserName(userData.name);
            setUserDescription(userData.about);
            setUserAvatar(userData.avatar);
        })
        .catch((err) => {
            console.log(err)
        }, [userName, userDescription, userAvatar])
    })
    return(
        <main className="content">
            <section className="profile">
                <div className="profile__avatar-group" onClick={props.onEditAvatar}>
                    <img alt="Аватар профиля" className="profile__avatar"/>
                </div>
                <div className="profile__info">
                    <h1 className="profile__name">Жак-Ив Кусто</h1>
                    <button type="button" className="profile__edit-button" onClick={props.onEditProfile}></button>
                    <p className="profile__bio">Исследователь океана</p>
                </div>
                <button type="button" className="profile__add-button" onClick={props.onAddPlace}></button>
            </section>
            <section className="cards">
                <template id="cards__item">
                    <div className="cards__item">
                        <button type="button" className="cards__button-delete"></button>
                        <img alt="Картинка" className="cards__pic"/>
                        <div className="cards__info">
                            <h2 className="cards__text"></h2>
                            <div className="cards__like-group">
                                <button type="button" className="cards__button-like"></button>
                                <span className="cards__like-count"></span>
                            </div>
                        </div>
                    </div>
                </template>
            </section>
        </main>
    )
}
